import React from "react";

const Home = () => {
  const featuredMovie = {
    title: "Interstellar",
    description:
      "A team of explorers travel through a wormhole in space in an attempt to ensure humanity's survival.",
    image:
      "https://upload.wikimedia.org/wikipedia/en/b/bc/Interstellar_film_poster.jpg",
  };

  const categories = [
    {
      name: "Sci-Fi",
      image:
        "https://images.unsplash.com/photo-1541701494587-cb58502866ab?auto=format&fit=crop&w=800&q=80",
    },
    {
      name: "Adventure",
      image:
    "data:image/jpeg;base64,"    },
    {
      name: "Romance",
      image:
"data:image/jpeg;base64,"    },
    {
      name: "Horror",
      image:
        "data:image/jpeg;base64,",
    },
  ];

  const styles = {
    container: {
      fontFamily: "Arial, sans-serif",
      backgroundColor: "#111",
      color: "#eee",
      minHeight: "100vh",
    },
    hero: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      gap: "20px",
      padding: "40px 20px",
      backgroundColor: "#1a1a1a",
    },
    heroContent: {
      maxWidth: "500px",
      textAlign: "center",
    },
    heroTitle: {
      fontSize: "2.5rem",
      color: "#00bcd4",
    },
    heroDesc: {
      margin: "15px 0",
    },
    button: {
      padding: "10px 20px",
      backgroundColor: "#00bcd4",
      color: "#fff",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
    },
    heroImg: {
      maxWidth: "300px",
      borderRadius: "8px",
    },
    section: {
      padding: "40px 20px",
      textAlign: "center",
    },
    sectionTitle: {
      fontSize: "2rem",
      color: "#00bcd4",
      marginBottom: "30px",
    },
    grid: {
      display: "grid",
      gap: "20px",
      gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    },
    card: {
      backgroundColor: "#222",
      borderRadius: "8px",
      padding: "10px",
      transition: "transform 0.3s",
    },
    cardImg: {
      width: "100%",
      height: "150px",
      objectFit: "cover",
      borderRadius: "4px",
    },
    cardTitle: {
      margin: "10px 0",
      color: "#00bcd4",
    },
    cardBtn: {
      padding: "6px 12px",
      backgroundColor: "transparent",
      border: "1px solid #00bcd4",
      color: "#00bcd4",
      borderRadius: "5px",
      cursor: "pointer",
    },
    footer: {
      padding: "20px",
      textAlign: "center",
      backgroundColor: "#1a1a1a",
      fontSize: "14px",
      color: "#aaa",
      marginTop: "40px",
    },
  };

  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>{featuredMovie.title}</h1>
          <p style={styles.heroDesc}>{featuredMovie.description}</p>
          <button style={styles.button}>Watch Now</button>
        </div>
        <img
          style={styles.heroImg}
          src={featuredMovie.image}
          alt={featuredMovie.title}
        />
      </section>

      {/* Categories */}
      <section style={styles.section}>
        <h2 style={styles.sectionTitle}>Explore by Genre</h2>
        <div style={styles.grid}>
          {categories.map((cat, i) => (
            <div key={i} style={styles.card}>
              <img src={cat.image} alt={cat.name} style={styles.cardImg} />
              <h3 style={styles.cardTitle}>{cat.name}</h3>
              <button style={styles.cardBtn}>Browse</button>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer style={styles.footer}>Â© 2025 MovieVerse. All rights reserved.</footer>
    </div>
  );
};

export default Home;
